import React from 'react';

export const Sidebar: React.FC = () => {
  return (
    <aside className="sidebar glass-card" style={{ width: '250px', height: '100vh', position: 'fixed', left: 0, top: 0 }}>
      <div className="logo" style={{ padding: '2rem', fontSize: '1.5rem', fontWeight: 'bold' }}>EcoSentinel</div>
      <nav>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {['Dashboard', 'Sensors', 'Analytics', 'Settings', 'Profile'].map(item => (
            <li key={item} style={{ padding: '1rem 2rem', cursor: 'pointer', transition: 'background 0.2s' }}>
              {item}
            </li>
          ))}
        </ul>
      </nav>
    </aside>
  );
};

import React from 'react';

interface Transaction {
  id: string;
  hash: string;
  status: 'pending' | 'success' | 'failed';
  amount: string;
}

export const TransactionList: React.FC<{ transactions: Transaction[] }> = ({ transactions }) => {
  return (
    <div className="transaction-list glass-card">
      <h3>Recent Activity</h3>
      {transactions.map(tx => (
        <div key={tx.id} style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', borderBottom: '1px solid rgba(255,255,255,0.1)' }}>
           <span>{tx.hash.substr(0, 10)}...</span>
           <span style={{ color: tx.status === 'success' ? '#4caf50' : '#f44336' }}>{tx.status}</span>
           <span>{tx.amount} STX</span>
        </div>
      ))}
    </div>
  );
};

import React from 'react';

export const TokenBalance: React.FC<{ symbol: string, balance: string, icon: string }> = ({ symbol, balance, icon }) => {
  return (
    <div className="token-card glass-card" style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
      <img src={icon} alt={symbol} style={{ width: '40px', height: '40px', borderRadius: '50%' }} />
      <div>
        <div style={{ fontSize: '0.9rem', color: 'var(--text-muted)' }}>{symbol} Balance</div>
        <div style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>{balance}</div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';

export const SettingsPage: React.FC = () => {
  const [notifications, setNotifications] = useState(true);
  const [darkMode, setDarkMode] = useState(true);
  const [autoConnect, setAutoConnect] = useState(false);

  return (
    <div className="settings-page glass-card" style={{ padding: '2rem', maxWidth: '600px', margin: '0 auto' }}>
      <h2>Settings</h2>

      <div className="setting-item" style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem 0' }}>
        <label>Enable Notifications</label>
        <input type="checkbox" checked={notifications} onChange={e => setNotifications(e.target.checked)} />
      </div>

      <div className="setting-item" style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem 0' }}>
        <label>Dark Mode</label>
        <input type="checkbox" checked={darkMode} onChange={e => setDarkMode(e.target.checked)} />
      </div>

      <div className="setting-item" style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem 0' }}>
        <label>Auto-Connect Wallet</label>
        <input type="checkbox" checked={autoConnect} onChange={e => setAutoConnect(e.target.checked)} />
      </div>

      <div style={{ marginTop: '2rem' }}>
        <button>Save Changes</button>
      </div>
    </div>
  );
};
