export interface UserProfile {
  stxAddress: string;
  domainName?: string;
  avatarUrl?: string;
  email?: string;
  bio?: string;
  createdAt: number;
  preferences: {
    theme: 'light' | 'dark';
    notifications: boolean;
  };
}

export interface Block {
  height: number;
  hash: string;
  parentHash: string;
  timestamp: number;
  txCount: number;
  miner: string;
  reward: number;
  difficulty: number;
}

import { StacksMainnet } from '@stacks/network';

const network = new StacksMainnet();

export const getAccountBalances = async (address: string) => {
  const response = await fetch(`${network.coreApiUrl}/extended/v1/address/${address}/balances`);
  return response.json();
};

export const getTransactions = async (address: string) => {
  const response = await fetch(`${network.coreApiUrl}/extended/v1/address/${address}/transactions`);
  return response.json();
};

export const getBlockInfo = async (height: number) => {
  const response = await fetch(`${network.coreApiUrl}/extended/v1/block/by_height/${height}`);
  return response.json();
};

import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface UserState {
  isAuthenticated: boolean;
  address: string | null;
  loading: boolean;
}

const initialState: UserState = {
  isAuthenticated: false,
  address: null,
  loading: false,
};

const userSlice = createSlice({
  name: 'user',
  initialState,
  reducers: {
    setAuthenticated(state, action: PayloadAction<boolean>) {
      state.isAuthenticated = action.payload;
    },
    setAddress(state, action: PayloadAction<string>) {
      state.address = action.payload;
    },
    setLoading(state, action: PayloadAction<boolean>) {
      state.loading = action.payload;
    },
  },
});

export const { setAuthenticated, setAddress, setLoading } = userSlice.actions;
export default userSlice.reducer;

import { render, screen } from '@testing-library/react';
import { Sidebar } from '../../frontend/src/components/Sidebar';

test('renders dashboard link', () => {
  render(<Sidebar />);
  const linkElement = screen.getByText(/Dashboard/i);
  expect(linkElement).toBeInTheDocument();
});

test('renders settings link', () => {
  render(<Sidebar />);
  const linkElement = screen.getByText(/Settings/i);
  expect(linkElement).toBeInTheDocument();
});
