import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';

// https://vitejs.dev/config/
export default defineConfig({
  plugins: [react()],
});

:root {
  --primary: 250 100% 65%;
  --secondary: 180 100% 40%;
  --dark-bg: 240 10% 10%;
  --card-bg: 240 10% 15%;
  --text-main: 0 0% 100%;
  --text-muted: 240 10% 70%;
}

body {
  margin: 0;
  font-family: 'Inter', system-ui, sans-serif;
  background: hsl(var(--dark-bg));
  color: hsl(var(--text-main));
  min-height: 100vh;
  overflow-x: hidden;
}

.gradient-bg {
  background: radial-gradient(circle at 50% 0%, hsl(var(--primary) / 0.2), transparent 70%);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 100vh;
  z-index: -1;
}

.glass-card {
  background: hsl(var(--card-bg) / 0.6);
  backdrop-filter: blur(12px);
  border: 1px solid hsl(var(--text-main) / 0.1);
  border-radius: 16px;
  padding: 2rem;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.glass-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 30px -10px hsl(var(--primary) / 0.3);
  border-color: hsl(var(--primary) / 0.5);
}

button {
  background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--secondary)));
  border: none;
  border-radius: 8px;
  padding: 0.8rem 1.5rem;
  color: white;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
  font-size: 1rem;
}

button:hover {
  opacity: 0.9;
  transform: scale(1.02);
  box-shadow: 0 0 15px hsl(var(--primary) / 0.5);
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App.tsx';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')!).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
);

import React, { useState, useEffect } from 'react';
import { AppConfig, UserSession, showConnect } from '@stacks/connect';
import { StacksTestnet } from '@stacks/network';

const appConfig = new AppConfig(['store_write', 'publish_data']);
const userSession = new UserSession({ appConfig });

function App() {
  const [userData, setUserData] = useState<any>(null);

  useEffect(() => {
    if (userSession.isSignInPending()) {
      userSession.handlePendingSignIn().then((userData) => {
        setUserData(userData);
      });
    } else if (userSession.isUserSignedIn()) {
      setUserData(userSession.loadUserData());
    }
  }, []);

  const connectWallet = () => {
    showConnect({
      appDetails: {
        name: 'Ecosentinel',
        icon: window.location.origin + '/vite.svg',
      },
      redirectTo: '/',
      onFinish: () => {
        window.location.reload();
      },
      userSession,
    });
  };

  const disconnect = () => {
    userSession.signUserOut('/');
    setUserData(null);
  };

  return (
    <div className="app-container">
      <div className="gradient-bg" />
      <header style={{ padding: '2rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', background: 'linear-gradient(to right, #fff, #aaa)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
          Ecosentinel
        </h1>
        {userData ? (
          <button onClick={disconnect}>Disconnect</button>
        ) : (
          <button onClick={connectWallet}>Connect Wallet</button>
        )}
      </header>

      <main style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
        <div className="glass-card">
          <h2>Welcome to Ecosentinel</h2>
          <p style={{ color: 'var(--text-muted)', lineHeight: '1.6' }}>
            The next generation of blockchain monitoring and interaction.
            Seamlessly connect your Stacks wallet and interact with the ecosystem.
          </p>

          {userData && (
            <div style={{ marginTop: '2rem', padding: '1rem', background: 'rgba(0,0,0,0.2)', borderRadius: '8px' }}>
              <p>Connected as: <code>{userData.profile.stxAddress.testnet}</code></p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
