import React from 'react';
import { ChainhookMonitor } from '../services/chainhooksService';

export const ChainhooksView: React.FC = () => {
  const monitor = new ChainhookMonitor();

  return (
    <div className="glass-card">
      <h3>Chainhooks Status</h3>
      <p>Monitoring active hooks...</p>
      <button onClick={() => monitor.listen()}>Start Listening</button>
    </div>
  );
};

import React, { useState } from 'react';
import { initWalletConnect, pair } from '../services/walletConnectService';

export const ConnectWalletWC: React.FC = () => {
  const [uri, setUri] = useState('');

  const handleConnect = async () => {
    if (!uri) return;
    try {
      await pair(uri);
      alert('Paired!');
    } catch (e) {
      console.error(e);
    }
  };

  return (
    <div className="glass-card" style={{ marginTop: '1rem' }}>
      <h3>Wallet Connect (Standard)</h3>
      <input 
        type="text" 
        placeholder="wc: uri" 
        value={uri}
        onChange={e => setUri(e.target.value)}
        style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #444', background: '#222', color: 'white', width: '100%', marginBottom: '1rem' }}
      />
      <button onClick={handleConnect}>Pair Wallet</button>
    </div>
  );
};
