import React, { createContext, useContext, useState, useCallback } from 'react';

type NotificationType = 'success' | 'error' | 'info';

interface Notification {
  id: string;
  type: NotificationType;
  message: string;
}

interface NotificationContextType {
  notifications: Notification[];
  addNotification: (type: NotificationType, message: string) => void;
  removeNotification: (id: string) => void;
}

const NotificationContext = createContext<NotificationContextType | undefined>(undefined);

export const NotificationProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [notifications, setNotifications] = useState<Notification[]>([]);

  const addNotification = useCallback((type: NotificationType, message: string) => {
    const id = Math.random().toString(36).substr(2, 9);
    setNotifications(prev => [...prev, { id, type, message }]);
    setTimeout(() => removeNotification(id), 5000);
  }, []);

  const removeNotification = useCallback((id: string) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  }, []);

  return (
    <NotificationContext.Provider value={{ notifications, addNotification, removeNotification }}>
      {children}
    </NotificationContext.Provider>
  );
};

export const useNotifications = () => {
  const context = useContext(NotificationContext);
  if (!context) throw new Error('useNotifications must be used within a NotificationProvider');
  return context;
};

import React from 'react';
import { useNotifications } from '../context/NotificationContext';

export const ToastContainer: React.FC = () => {
  const { notifications, removeNotification } = useNotifications();

  return (
    <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000, display: 'flex', flexDirection: 'column', gap: '10px' }}>
      {notifications.map(n => (
        <div 
          key={n.id} 
          onClick={() => removeNotification(n.id)}
          className="glass-card"
          style={{
            padding: '1rem',
            borderLeft: `4px solid ${n.type === 'success' ? '#4caf50' : n.type === 'error' ? '#f44336' : '#2196f3'}`,
            minWidth: '250px',
            cursor: 'pointer',
            animation: 'slideIn 0.3s ease'
          }}
        >
          {n.message}
        </div>
      ))}
    </div>
  );
};

import React from 'react';

interface DataPoint {
  timestamp: number;
  value: number;
}

export const DataChart: React.FC<{ data: DataPoint[], title: string }> = ({ data, title }) => {
  const maxVal = Math.max(...data.map(d => d.value));

  return (
    <div className="glass-card" style={{ marginTop: '20px', padding: '20px' }}>
      <h3>{title}</h3>
      <div style={{ display: 'flex', alignItems: 'flex-end', height: '200px', gap: '4px', paddingTop: '20px' }}>
        {data.map((point, i) => (
          <div 
            key={i} 
            style={{
              flex: 1,
              background: `linear-gradient(to top, hsl(var(--primary) / 0.8), hsl(var(--secondary) / 0.8))`,
              height: `${(point.value / maxVal) * 100}%`,
              borderRadius: '4px 4px 0 0',
              transition: 'height 0.3s ease',
              minWidth: '8px'
            }}
            title={`Value: ${point.value}`}
          />
        ))}
      </div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px', color: 'var(--text-muted)', fontSize: '0.8rem' }}>
        <span>Past 24h</span>
        <span>Now</span>
      </div>
    </div>
  );
};

export const generateMockData = (count: number) => {
  return Array.from({ length: count }, (_, i) => ({
    timestamp: Date.now() - (count - i) * 3600000,
    value: Math.floor(Math.random() * 100)
  }));
};

export const SENSOR_TYPES = ['Temperature', 'Humidity', 'Air Quality', 'Noise Level'];

import React from 'react';

export const MapOverview: React.FC = () => {
  return (
    <div className="glass-card" style={{ height: '400px', position: 'relative', overflow: 'hidden', marginTop: '2rem' }}>
      <div style={{ position: 'absolute', top: '20px', left: '20px', zIndex: 10 }}>
        <h3>Network Topology</h3>
        <p style={{ color: 'var(--text-muted)' }}>Active Nodes: 124</p>
      </div>

      {/* Abstract Map Visualization Grid */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(10, 1fr)', 
        height: '100%', 
        opacity: 0.3 
      }}>
        {Array.from({ length: 100 }).map((_, i) => (
          <div key={i} style={{
            border: '1px solid rgba(255,255,255,0.05)',
            position: 'relative'
          }}>
            {Math.random() > 0.9 && (
              <div style={{
                width: '8px', height: '8px',
                background: '#00ff88',
                borderRadius: '50%',
                position: 'absolute',
                top: '50%', left: '50%',
                transform: 'translate(-50%, -50%)',
                boxShadow: '0 0 10px #00ff88'
              }} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

import React from 'react';
